import React, { useEffect, useState } from "react";
import styles from "../../styles/cabinet/CabinetNewTest.module.css";

const CabinetNewTest = ({ onTabChange }) => {
  const [questions, setQuestions] = useState([
    { question: "", answers: [""], correctAnswerIndex: -1 },
  ]);

  const handleTabChange = (tab) => {
    onTabChange(tab);
  };

  const handleAddQuestion = () => {
    setQuestions([
      ...questions,
      { question: "", answers: [""], correctAnswerIndex: -1 },
    ]);
  };

  const handleQuestionChange = (index, value) => {
    const updatedQuestions = [...questions];
    updatedQuestions[index].question = value;
    setQuestions(updatedQuestions);
  };

  const handleAddAnswer = (questionIndex) => {
    const updatedQuestions = [...questions];
    updatedQuestions[questionIndex].answers.push("");
    setQuestions(updatedQuestions);
  };

  const handleAnswerChange = (questionIndex, answerIndex, value) => {
    const updatedQuestions = [...questions];
    updatedQuestions[questionIndex].answers[answerIndex] = value;
    setQuestions(updatedQuestions);
  };

  const handleCorrectAnswerChange = (questionIndex, answerIndex) => {
    const updatedQuestions = [...questions];
    updatedQuestions[questionIndex].correctAnswerIndex = answerIndex;
    setQuestions(updatedQuestions);
  };

  return (
    <>
      <h2>Новий тест</h2>
      <button className={styles.newQuestionButton} onClick={handleAddQuestion}>
        + Додати запитання
      </button>
      <ul className={styles.questionsList}>
        {questions.map((question, questionIndex) => (
          <li key={questionIndex}>
            <input
              type="text"
              placeholder="Введіть запитання"
              value={question.question}
              onChange={(e) =>
                handleQuestionChange(questionIndex, e.target.value)
              }
            />
            <ul className={styles.answersList}>
              {question.answers.map((answer, answerIndex) => (
                <li key={answerIndex}>
                  <input
                    type="text"
                    placeholder="Введіть відповідь"
                    value={answer}
                    onChange={(e) =>
                      handleAnswerChange(
                        questionIndex,
                        answerIndex,
                        e.target.value
                      )
                    }
                  />
                  <input
                    type="checkbox"
                    checked={question.correctAnswerIndex === answerIndex}
                    onChange={() =>
                      handleCorrectAnswerChange(questionIndex, answerIndex)
                    }
                  />
                </li>
              ))}
              <button
                className={styles.newQuestionButton}
                onClick={() => handleAddAnswer(questionIndex)}
              >
                + Додати відповідь
              </button>
            </ul>
          </li>
        ))}
      </ul>
      <div className={styles.buttonsContainer}>
        <button
          className={styles.backButton}
          onClick={() => handleTabChange("tests")}
        >
          Назад
        </button>
        <button
          className={styles.backButton}
          onClick={() => handleTabChange("tests")}
        >
          Зберегти
        </button>
      </div>
    </>
  );
};

export default CabinetNewTest;
