import { useLocation, useNavigate } from "react-router-dom";
import styles from "../../styles/cabinet/CabinetTestStats.module.css";
import style from "../../styles/cabinet/CabinetPage.module.css";
import { useEffect, useState } from "react";
import { getTestName, getUserTestByTestId } from "../../services/TestApi";
import { TestStatRaitingCart } from "../TestStatRaitingCart";
import { TestStatTimeCart } from "../TestStatTimeCart";

const CabinetTestStat = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const testId = location.state.data;

  const [test, setTest] = useState();
  const [allStatsSortedByRaiting, setAllStatsSortedByRaiting] = useState([]);
  const [allStatsSortedByTime, setAllStatsSortedByTime] = useState([]);

  useEffect(() => {
    getTestName(testId).then((data) => setTest(data));
    getUserTestByTestId(testId).then((data) => {
      setAllStatsSortedByRaiting(data);
      setAllStatsSortedByTime(data);
    });
  }, [testId, allStatsSortedByRaiting]);

  setAllStatsSortedByRaiting(
    allStatsSortedByRaiting.sort(
      (a, b) => b.CorrectAnswerCount - a.CorrectAnswerCount
    )
  );

  setAllStatsSortedByTime(
    allStatsSortedByTime.sort((a, b) => {
      const [minutesA, secondsA] = a.Time.split(":");
      const [minutesB, secondsB] = b.Time.split(":");
      const minutesInMillisecondsA = parseInt(minutesA) * 60000;
      const secondsInMillisecondsA = parseInt(secondsA) * 1000;
      const minutesInMillisecondsB = parseInt(minutesB) * 60000;
      const secondsInMillisecondsB = parseInt(secondsB) * 1000;
      const totalMillisecondsA =
        minutesInMillisecondsA + secondsInMillisecondsA;
      const totalMillisecondsB =
        minutesInMillisecondsB + secondsInMillisecondsB;
      return totalMillisecondsA - totalMillisecondsB;
    })
  );

  if (!test || !allStatsSortedByTime || !allStatsSortedByRaiting) {
    return <>loading</>;
  }

  return (
    <div className={style.CabinetPageBody}>
      <div className={style.block}>
        <h2>Статистика по тесту: {test.Title}</h2>
        <ul className={styles.statList}>
          <li className={styles.statBox}>
            <h3>Топ по оцінкам</h3>
            <ul className={styles.smallList}>
              {allStatsSortedByRaiting.map((element, index) => (
                <TestStatRaitingCart stat={element} index={index} />
              ))}
            </ul>
          </li>
          <li className={styles.statBox}>
            <h3>Топ по часу</h3>
            <ul className={styles.smallList}>
              {allStatsSortedByTime.map((element, index) => (
                <TestStatTimeCart stat={element} index={index} />
              ))}
            </ul>
          </li>
        </ul>
        <button
          className={styles.backButton}
          onClick={() => navigate("/cabinet")}>
          Назад
        </button>
      </div>
    </div>
  );
};

export default CabinetTestStat;
